mapper.select:
select

from order_info
where order_type = #{orderType,jdbcType=CHAR}
and user_id = #{userId,jdbcType=BIGINT}
and is_active = #{isActive,jdbcType=CHAR}
and is_del = '0'

select count(1)
from order_info
where order_type = #{orderType,jdbcType=CHAR}
  and is_active = #{isActive,jdbcType=CHAR}
  and is_del = '0'

select

from order_info
where order_type = #{orderType,jdbcType=CHAR}
and is_active = #{isActive,jdbcType=CHAR}
and is_del = '0'
order by create_time DESC
limit #{startIndex,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

